/**
 * Remove units in zero values, except when required: in angles and timings,
 * in which case make them consistent 0deg and 0s.
 */

declare function normalizeZeroDimensions(
  ast: PostCSSValueAST,
  _: unknown,
): PostCSSValueAST;
export default normalizeZeroDimensions;
